 design based on Wells Rich Greene\'s advertising campaign.'),(45371,177,'wp-preview',''),(45372,177,'hidden_post_status','publish'),(45373,177,'post_status','publish'),(45374,177,'hidden_post_password',''),(45375,177,'hidden_post_visibility','public'),(45376,177,'visibility','public'),(45377,177,'post_password',''),(45378,177,'mm','05'),(45379,177,'jj','27'),(45380,177,'aa','2014'),(45381,177,'hh','10'),(45382,177,'mn','50'),(45383,177,'ss','14'),(45384,177,'hidden_mm','05'),(45385,177,'cur_mm','03'),(45386,177,'hidden_jj','27'),(45387,177,'cur_jj','08'),(45388,177,'hidden_aa','2014'),(45389,177,'cur_aa','2016'),(45390,177,'hidden_hh','10'),(45391,177,'cur_hh','11'),(45392,177,'hidden_mn','50'),(45393,177,'cur_mn','37'),(45394,177,'original_publish','Update'),(45395,177,'publish','Publish'),(45396,177,'newcategory','New Category Name'),(45397,177,'newcategory_parent','-1'),(45398,177,'_ajax_nonce-add-category','698332b097'),(45399,177,'excerpt',''),(45400,177,'trackback_url',''),(45401,177,'metakeyselect','#NONE#'),(45402,177,'metakeyinput',''),(45403,177,'metavalue',''),(45404,177,'_ajax_nonce-add-meta','bd4efe1329'),(45405,177,'advanced_view','1'),(45406,177,'comment_status','open'),(45407,177,'ping_status','open'),(45408,177,'post_name','full-width-post'),(45409,177,'post_author_override','1'),(45410,177,'slide_template','default'),(45411,177,'estate_property_noncename','2a25352f84'),(45412,177,'post_show_title','yes'),(45413,177,'group_pictures','no'),(45414,177,'embed_video_id',''),(45415,177,'embed_video_type','vimeo'),(45416,177,'header_type','1'),(45417,177,'page_custom_lat',''),(45418,177,'page_custom_long',''),(45419,177,'page_custom_zoom','15'),(45420,177,'min_height','0'),(45421,177,'max_height','0'),(45422,177,'keep_min','yes'),(45423,177,'page_custom_image',''),(45424,177,'rev_slider',''),(45425,177,'wpestate_sidebar_noncename','2a25352f84'),(45426,177,'sidebar_option','none'),(45427,177,'sidebar_select','primary-widget-area'),(45428,177,'post_mime_ty